package Service;

import Model.Product;

import java.util.ArrayList;
import java.util.List;

public class ProducService {
    public List<Product> list = new ArrayList<>();

    public ProducService() {
        list.add(new Product(1, "OPP", "30000", "https://cellphones.com.vn/sforum/wp-content/uploads/2020/04/thong-tin-OPPO-A12-1.jpg", "99%"));
        list.add(new Product(2, "Iphone", "20000", "data:image/jpeg;base64,", "100%"));
        list.add(new Product(3, "SamSung", "70000", "data:image/jpeg;base64,", "99%"));
    }

    public void add(Product product) {
        list.add(product);
    }

    public void edit(int id, Product product) {
        list.set(id, product);
    }

    public void remove(int id) {
        list.remove(id);
    }

    public List<Product> find(String name) {
        ArrayList<Product> list1 = new ArrayList<>();
        for (Product p : list) {
            if (p.getName().contains(name)) {
                list1.add(p);
            }
        }
        return list1;
    }
}
